import React, { useState, useEffect } from 'react';
import { Link as ScrollLink } from 'react-scroll';
// import Sidebarr from './Sidebarr'; // Import Sidebar component

const Navbar = () => {
    const [isHidden, setIsHidden] = useState(false);
    // const [isSidebarOpen, setIsSidebarOpen] = useState(false); // Sidebar state logic commented out
    let lastScrollTop = 0;

    useEffect(() => {
        const handleScroll = () => {
            const currentScrollTop = window.pageYOffset || document.documentElement.scrollTop;

            if (currentScrollTop > lastScrollTop) {
                setIsHidden(true);
            } else {
                setIsHidden(false);
            }

            lastScrollTop = currentScrollTop <= 0 ? 0 : currentScrollTop;
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, [lastScrollTop]);

    // const handleSidebarToggle = () => {
    //     setIsSidebarOpen(!isSidebarOpen); // Sidebar toggle logic commented out
    // };

    return (
        <>
            <nav className={`navbar ${isHidden ? 'hidden' : ''}`}>
                {/* <button className="toggle-button" onClick={handleSidebarToggle}> */}
                {/*     â˜° */}
                {/* </button> */}
                <ul className="navbar-menu">
                    <li><ScrollLink to="home" smooth={true} duration={500}>Home</ScrollLink></li>
                    <li><ScrollLink to="about-us" smooth={true} duration={500}>About Us</ScrollLink></li>
                    <li><ScrollLink to="services" smooth={true} duration={500}>Services</ScrollLink></li>
                    <li><ScrollLink to="clients" smooth={true} duration={500}>Clients</ScrollLink></li>
                    <li><ScrollLink to="why-choose-us" smooth={true} duration={500}>Why Choose Us</ScrollLink></li>
                </ul>
            </nav>
            {/* <Sidebarr isOpen={isSidebarOpen} handleClose={handleSidebarToggle} /> */}
        </>
    );
};

export default Navbar;
