import React from "react";
import "./ServicesPage.css";

const Services = () => {
    const services = [
        { title: "Strategic Marketing", description: "Crafting strategies to put your brand ahead in the game." },
        { title: "Social Media Management", description: "Your brand, trending across all platforms with style." },
        { title: "SEO", description: "Boost your online swagger with SEO expertise." },
        { title: "Creative Design", description: "From logos to ad campaigns, we make ideas come alive." },
        { title: "Technical Solutions", description: "Tech-savvy fixes to keep your business running smoothly." },
        { title: "Video Editing", description: "Captivating stories told through stunning visuals." },
        { title: "Website Development", description: "Modern, responsive websites tailored to your brand." },
        { title: "Application Development", description: "Mobile apps that captivate and perform." },
    ];

    // Function to handle hover events
    const handleHover = (isHovering) => {
        const carouselTrack = document.querySelector(".carousel-track");
        carouselTrack.style.animationPlayState = isHovering ? "paused" : "running";
    };

    return (
        <section id="services" className="services-page">
            <h2>Our Services</h2>
            <div
                className="carousel-container"
                onMouseEnter={() => handleHover(true)}
                onMouseLeave={() => handleHover(false)}
            >
                <div className="carousel-track">
                    {services.map((service, index) => (
                        <div className="carousel-item" key={index}>
                            <h3>{service.title}</h3>
                            <p>{service.description}</p>
                        </div>
                    ))}
                </div>
            </div>
        </section>
    );
};

export default Services;
